package com.adventofcode;

import java.util.stream.Stream;

import static java.util.Objects.isNull;
import static org.assertj.core.api.Assertions.assertThat;

import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

class Question06Test {

    public static final int ANSWER_1 = 1034;
    public static final int ANSWER_2 = 2472;
    String source;

    @BeforeEach
    void setUpSource() {
        source = "";
    }

    @Test
    void example() {
        assertThat(allUniqueCharacters("abcd")).isTrue();
        assertThat(allUniqueCharacters("abca")).isFalse();
    }

    @Test
    void question1() {
        int endOfFirstMarker = findEndOfFirstMarker(4);
        assertThat(endOfFirstMarker).isEqualTo(ANSWER_1);
    }

    @Test
    void question2() {
        int endOfFirstMarker = findEndOfFirstMarker(14);
        assertThat(endOfFirstMarker).isEqualTo(ANSWER_2);
    }


    int findEndOfFirstMarker(int length) {
        for (int i = length - 1; i < source.length(); i++) {

            String substring = getSubstring(i, length);
            if (!isNull(substring) && allUniqueCharacters(substring)) {
                return i + 1;
            }
        }
        throw new IllegalArgumentException("No match found");
    }

    private boolean allUniqueCharacters(String input) {
        Stream<String> distinct = input.codePoints().mapToObj(String::valueOf).distinct();
        return distinct.count() == input.length();
    }

    public String getSubstring(int endIndex, int length) {
        // Check if the end index is out of bounds
        if (endIndex < 0 || endIndex > source.length() - 1) {
            return null;
        }

        int startIndex = endIndex - length + 1;

        return source.substring(startIndex, endIndex + 1);
    }
}
